import React, { useState, useEffect } from 'react';
import { Moon, Sun, Github, Linkedin, Mail, MapPin } from 'lucide-react';
import foto from '../assets/background.png';
import libro from '../assets/ebook-example.jpg';
import spice from '../assets/spice.webp';
import easyCards from '../assets/easy_4.png';

const AnimatedText = ({ text }) => {
  return (
    <div className="inline-block">
      {text.split('').map((char, index) => (
        <span
          key={index}
          className="inline-block animate-bounce"
          style={{ animationDelay: `${index * 0.1}s` }}
        >
          {char}
        </span>
      ))}
    </div>
  );
};

const Portfolio = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isVisible, setIsVisible] = useState({});
  const [hoveredProject, setHoveredProject] = useState(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          setIsVisible((prev) => ({
            ...prev,
            [entry.target.id]: entry.isIntersecting
          }));
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('section').forEach((section) => {
      observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  const baseClasses = darkMode 
    ? 'min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900 text-white' 
    : 'min-h-screen bg-gradient-to-br from-purple-100 via-pink-100 to-purple-200 text-gray-900';

  const projects = [
    {
      title: "Project Sphere",
      description: "Aplicaci贸n web para gestionar proyectos personales con React",
      tech: ["React", "JavaScript", "CSS"],
      image: libro,
      link: "https://diana-bernardos.github.io/project-promo-X-module-3-team-2/"
    },
    {
      title: "Spice Developers",
      description: "Aplicaci贸n m贸vil para la gesti贸n de agendas familiares",
      tech: ["React", "JavaScript", "CSS"],
      image: spice,
      link: "https://ellisoalgo.github.io/project-grupo-2/"
    },
    {
      title: "Easy Peasy Cards",
      description: "Generador de tarjetas de visita personalizadas",
      tech: ["JavaScript", "CSS", "HTML"],
      image: easyCards,
      link: "https://aidanarr.github.io/project-promo-X-module-2-team-3/"
    }
  ];

  return (
    <div className={`${baseClasses} transition-all duration-500`}>
      <nav className="fixed top-0 w-full bg-gradient-to-r from-purple-600 via-pink-500 to-purple-600 text-white shadow-lg z-50 backdrop-blur-sm bg-opacity-90">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <a href="https://github.com/Diana-Bernardos" target="_blank" rel="noopener noreferrer" 
               className="hover:scale-110 transition-transform duration-300">
              <Github size={24} />
            </a>
            <a href="https://www.linkedin.com/in/diana-bernardos-moraleda-536378223" target="_blank" rel="noopener noreferrer"
               className="hover:scale-110 transition-transform duration-300">
              <Linkedin size={24} />
            </a>
            <a href="mailto:dianabernardosm@gmail.com" 
               className="hover:scale-110 transition-transform duration-300">
              <Mail size={24} />
            </a>
          </div>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full hover:bg-white/20 transition-all duration-300"
          >
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>
      </nav>

      <main className="container mx-auto px-6 pt-24 pb-12">
        <section id="home" className="text-center pt-12 mb-20">
          <h1 className="text-4xl font-bold mb-4">
            <AnimatedText text="Diana Bernardos" />
          </h1>
          <h2 className="text-3xl bg-gradient-to-r from-purple-500 to-pink-500 text-transparent bg-clip-text mb-8">
            <AnimatedText text="Full Stack Developer" />
          </h2>

          <div className="relative w-64 h-64 mx-auto mb-8 group">
            <img
              src={foto}
              alt="Diana Bernardos"
              className="rounded-full w-full h-full object-cover shadow-2xl border-4 border-purple-500 
                       group-hover:border-pink-500 transition-all duration-500 
                       group-hover:scale-105 transform"
            />
            <div className="absolute inset-0 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 opacity-0 
                          group-hover:opacity-20 transition-opacity duration-500"></div>
          </div>

          <p className="text-lg max-w-2xl mx-auto leading-relaxed mb-12 animate-fade-in">
            Profesional con experiencia en desarrollo web y aplicaciones, 
            especializada en React y JavaScript.
          </p>
        </section>

        <section id="projects" className={`mb-20 transform transition-all duration-1000 ${
          isVisible.projects ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
        }`}>
          <h2 className="text-3xl font-bold mb-12 text-center bg-gradient-to-r from-purple-500 to-pink-500 text-transparent bg-clip-text">
            Proyectos Destacados
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <a 
                href={project.link} 
                target="_blank" 
                rel="noopener noreferrer" 
                key={index}
                className="group bg-white/10 backdrop-blur-sm rounded-xl overflow-hidden shadow-xl 
                         hover:shadow-2xl transition-all duration-500 transform hover:scale-105"
                onMouseEnter={() => setHoveredProject(index)}
                onMouseLeave={() => setHoveredProject(null)}
              >
                <div className="relative overflow-hidden">
                  <img 
                    src={project.image} 
                    alt={project.title} 
                    className="w-full h-48 object-cover transform group-hover:scale-110 transition-transform duration-500" 
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent 
                                opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                </div>
                <div className="p-6">
                  <h3 className="text-xl font-bold mb-2 bg-gradient-to-r from-purple-500 to-pink-500 
                               text-transparent bg-clip-text">{project.title}</h3>
                  <p className="mb-4">{project.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span 
                        key={i} 
                        className="px-3 py-1 bg-gradient-to-r from-purple-500 to-pink-500 
                                 text-white rounded-full text-sm transform hover:scale-105 
                                 transition-transform duration-300"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              </a>
            ))}
          </div>
        </section>

        <section id="contact" className={`transform transition-all duration-1000 ${
          isVisible.contact ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
        }`}>
          <h2 className="text-3xl font-bold mb-8 text-center bg-gradient-to-r from-purple-500 to-pink-500 
                       text-transparent bg-clip-text">Contacto</h2>
          <div className="max-w-2xl mx-auto bg-white/10 backdrop-blur-sm rounded-xl p-8 
                        shadow-xl hover:shadow-2xl transition-all duration-500">
            <div className="flex flex-col gap-6">
              <a 
                href="mailto:dianabernardosm@gmail.com" 
                className="flex items-center gap-4 hover:text-purple-400 transition-colors duration-300 group"
              >
                <Mail className="text-purple-500 group-hover:text-pink-500 transition-colors duration-300" />
                dianabernardosm@gmail.com
              </a>
              <div className="flex items-center gap-4 group">
                <MapPin className="text-purple-500 group-hover:text-pink-500 transition-colors duration-300" />
                <span>Madrid, Espa帽a</span>
              </div>
            </div>
          </div>
        </section>
