import React, { useState, useEffect } from 'react';
import { Moon, Sun, Github, Linkedin, Mail, Phone, MapPin } from 'lucide-react';
import foto from '../assets/background.png';
import libro from '../assets/ebook-example.jpg';
import spice from '../assets/spice.webp';
import easyCards from '../assets/easy_4.png';

const Portfolio = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isVisible, setIsVisible] = useState({});

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          setIsVisible((prev) => ({
            ...prev,
            [entry.target.id]: entry.isIntersecting
          }));
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('section').forEach((section) => {
      observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  const baseClasses = darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900';

  const projects = [
    {
      title: "Project Sphere",
      description: "Aplicación web para gestionar proyectos personales con React",
      tech: ["React", "JavaScript", "CSS"],
      image: libro,
      link: "https://github.com/tu-usuario/project-sphere"
    },
    {
      title: "CrazyMom",
      description: "Aplicación móvil para la gestión de agendas familiares",
      tech: ["React", "JavaScript", "CSS"],
      image: spice,
      link: "https://github.com/tu-usuario/crazy-mom"
    },
    {
      title: "Easy Peasy Cards",
      description: "Generador de tarjetas de visita personalizadas",
      tech: ["JavaScript", "CSS", "HTML"],
      image: easyCards,
      link: "https://github.com/tu-usuario/easy-peasy-cards"
    }
  ];

  return (
    <div className={`min-h-screen ${baseClasses}`}>
      <nav className="fixed top-0 w-full bg-purple-600 text-white shadow-lg z-50">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">Diana Bernardos</h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full hover:bg-purple-700"
          >
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>
      </nav>

      <main className="container mx-auto px-6 pt-24 pb-12">
        <section id="home" className={`mb-20 ${isVisible.home ? 'opacity-100' : 'opacity-0'} transition-opacity duration-1000`}>
          <div className="flex flex-col md:flex-row items-center gap-12">
            <div className="md:w-1/2">
              <img
                src={foto}
                alt="Diana Bernardos"
                className="rounded-full w-64 h-64 object-cover mx-auto shadow-2xl border-4 border-purple-500"
              />
            </div>
            <div className="md:w-1/2 text-center md:text-left">
              <h2 className="text-4xl font-bold mb-4">Full Stack Developer</h2>
              <p className="text-lg mb-6">
                Profesional con experiencia en desarrollo web y aplicaciones, especializada en React y JavaScript.
              </p>
              <div className="flex gap-4 justify-center md:justify-start">
                <a href="https://github.com/tu-usuario" target="_blank" rel="noopener noreferrer" className="p-2 bg-purple-600 rounded-full hover:bg-purple-700">
                  <Github size={24} className="text-white" />
                </a>
                <a href="https://www.linkedin.com/in/tu-perfil" target="_blank" rel="noopener noreferrer" className="p-2 bg-purple-600 rounded-full hover:bg-purple-700">
                  <Linkedin size={24} className="text-white" />
                </a>
                <a href="mailto:tu@email.com" className="p-2 bg-purple-600 rounded-full hover:bg-purple-700">
                  <Mail size={24} className="text-white" />
                </a>
              </div>
            </div>
          </div>
        </section>

        <section id="projects" className={`mb-20 ${isVisible.projects ? 'opacity-100' : 'opacity-0'} transition-opacity duration-1000`}>
          <h2 className="text-3xl font-bold mb-8 text-center">Proyectos Destacados</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <a 
                href={project.link} 
                target="_blank" 
                rel="noopener noreferrer" 
                key={index}
                className="bg-purple-100 dark:bg-purple-900 rounded-lg overflow-hidden shadow-lg hover:shadow-2xl transition-shadow duration-300"
              >
                <img src={project.image} alt={project.title} className="w-full h-48 object-cover" />
                <div className="p-6">
                  <h3 className="text-xl font-bold mb-2">{project.title}</h3>
                  <p className="mb-4">{project.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span key={i} className="px-3 py-1 bg-purple-500 text-white rounded-full text-sm">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              </a>
            ))}
          </div>
        </section>

        <section id="contact" className={`${isVisible.contact ? 'opacity-100' : 'opacity-0'} transition-opacity duration-1000`}>
          <h2 className="text-3xl font-bold mb-8 text-center">Contacto</h2>
          <div className="max-w-2xl mx-auto bg-purple-100 dark:bg-purple-900 rounded-lg p-8">
            <div className="flex flex-col gap-4">
              <a href="mailto:tu@email.com" className="flex items-center gap-4 hover:text-purple-600">
                <Mail className="text-purple-600" />
                diana@email.com
              </a>
              <div className="flex items-center gap-4">
                <Phone className="text-purple-600" />
                <span>+34 XXX XXX XXX</span>
              </div>
              <div className="flex items-center gap-4">
                <MapPin className="text-purple-600" />
                <span>Madrid, España</span>
              </div>
            </div>
          </div>
        </section>
      </main>

      <footer className={`${baseClasses} border-t`}>
        <div className="container mx-auto px-6 py-4 text-center">
          <p>&copy; 2024 Diana Bernardos. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  );
};

export default Portfolio;
