import React, { useState, useEffect } from 'react';
import { Moon, Sun, Github, Linkedin, Mail, MapPin } from 'lucide-react';
import foto from '../assets/background.png';
import libro from '../assets/ebook-example.jpg';
import spice from '../assets/spice.webp';
import easyCards from '../assets/easy_4.png';

const Portfolio = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isVisible, setIsVisible] = useState({});

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          setIsVisible((prev) => ({
            ...prev,
            [entry.target.id]: entry.isIntersecting
          }));
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('section').forEach((section) => observer.observe(section));
    return () => observer.disconnect();
  }, []);

  const baseClasses = darkMode 
    ? 'min-h-screen bg-gradient-to-br from-slate-900 via-rose-900 to-slate-900 text-gray-100' 
    : 'min-h-screen bg-gradient-to-br from-rose-50 via-pink-50 to-rose-50 text-gray-800';

  const projects = [
    {
      title: "Project Sphere",
      description: "Aplicación web para gestionar proyectos personales con React",
      tech: ["React", "JavaScript", "CSS"],
      image: libro,
      link: "https://diana-bernardos.github.io/project-promo-X-module-3-team-2/"
    },
    {
      title: "Spice Developers",
      description: "Aplicación móvil para la gestión de agendas familiares",
      tech: ["React", "JavaScript", "CSS"],
      image: spice,
      link: "https://ellisoalgo.github.io/project-grupo-2/"
    },
    {
      title: "Easy Peasy Cards",
      description: "Generador de tarjetas de visita personalizadas",
      tech: ["JavaScript", "CSS", "HTML"],
      image: easyCards,
      link: "https://aidanarr.github.io/project-promo-X-module-2-team-3/"
    }
  ];

  return (
    <div className={`${baseClasses} transition-all duration-500`}>
      <nav className="fixed top-0 w-full backdrop-blur-md bg-white/10 dark:bg-black/10 z-50">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <div className="flex items-center space-x-6">
            <a href="https://github.com/Diana-Bernardos" 
               target="_blank" 
               rel="noopener noreferrer" 
               className="hover:text-rose-400 transform hover:scale-110 transition-all duration-300">
              <Github size={20} />
            </a>
            <a href="https://www.linkedin.com/in/diana-bernardos-moraleda-536378223" 
               target="_blank" 
               rel="noopener noreferrer"
               className="hover:text-rose-400 transform hover:scale-110 transition-all duration-300">
              <Linkedin size={20} />
            </a>
            <a href="mailto:dianabernardosm@gmail.com" 
               className="hover:text-rose-400 transform hover:scale-110 transition-all duration-300">
              <Mail size={20} />
            </a>
          </div>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-xl bg-rose-300/10 hover:bg-rose-300/20 transition-all duration-300"
          >
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>
      </nav>

      <main className="container mx-auto px-6 pt-24 pb-12">
        <section id="home" className="flex flex-col items-center justify-center min-h-[80vh] py-16">
          <div className="relative mb-8 group">
            <div className="absolute -inset-1 bg-gradient-to-r from-rose-300 to-pink-300 rounded-full blur opacity-50 group-hover:opacity-75 transition duration-500"></div>
            <img
              src={foto}
              alt="Diana Bernardos"
              className="relative w-48 h-48 rounded-full object-cover shadow-2xl transition-transform duration-500 group-hover:scale-105"
            />
          </div>

          <h1 className="text-5xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-rose-400 to-pink-400">
            Diana Bernardos
          </h1>
          <h2 className="text-2xl mb-6 text-rose-400 dark:text-rose-300">
            Full Stack Developer
          </h2>
          <p className="text-lg max-w-2xl text-center leading-relaxed">
            Profesional con experiencia en desarrollo web y aplicaciones, 
            especializada en React y JavaScript.
          </p>
        </section>

        <section id="projects" className={`py-20 transition-all duration-700 transform ${
          isVisible.projects ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
        }`}>
          <h2 className="text-4xl font-bold mb-12 text-center bg-clip-text text-transparent bg-gradient-to-r from-rose-400 to-pink-400">
            Proyectos Destacados
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <a 
                href={project.link} 
                target="_blank" 
                rel="noopener noreferrer" 
                key={index}
                className="group relative bg-white/10 backdrop-blur-sm rounded-xl overflow-hidden hover:shadow-2xl transition-all duration-500"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-rose-300/10 to-pink-300/10 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                <div className="relative overflow-hidden">
                  <img 
                    src={project.image} 
                    alt={project.title} 
                    className="w-full h-48 object-cover transition-transform duration-500 group-hover:scale-110" 
                  />
                </div>
                <div className="p-6 relative z-10">
                  <h3 className="text-xl font-bold mb-2 text-rose-400 dark:text-rose-300">
                    {project.title}
                  </h3>
                  <p className="mb-4">{project.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span 
                        key={i} 
                        className="px-3 py-1 text-sm rounded-full bg-rose-100 dark:bg-rose-900/30 
                                 text-rose-600 dark:text-rose-300"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              </a>
            ))}
          </div>
        </section>

        <section id="contact" className={`py-20 transition-all duration-700 transform ${
          isVisible.contact ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'
        }`}>
          <h2 className="text-4xl font-bold mb-12 text-center bg-clip-text text-transparent bg-gradient-to-r from-rose-400 to-pink-400">
            Contacto
          </h2>
          <div className="max-w-xl mx-auto bg-white/10 backdrop-blur-sm rounded-xl p-8 shadow-lg hover:shadow-xl transition-all duration-300">
            <div className="flex flex-col gap-6">
              <a 
                href="mailto:dianabernardosm@gmail.com" 
                className="flex items-center gap-4 text-rose-400 dark:text-rose-300 hover:text-pink-400 transition-colors duration-300"
              >
                <Mail className="shrink-0" />
                dianabernardosm@gmail.com
              </a>
              <div className="flex items-center gap-4">
                <MapPin className="shrink-0 text-rose-400 dark:text-rose-300" />
                <span>Madrid, España</span>
              </div>
            </div>
          </div>
        </section>
      </main>

      <footer className="border-t border-rose-200/20">
        <div className="container mx-auto px-6 py-4 text-center text-rose-400/60 dark:text-rose-300/60">
          <p>&copy; 2024 Diana Bernardos. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  );
};

export default Portfolio;
